INSERT INTO `lm_news_tbl` (`NewsId`, `NewsTitle`, `NewsDetail`, `NewsDate`) VALUES
(1, 'PM for emission cut target with equitable burden sharing', 'Pushing for a legally binding substantive outcome at Copenhagen meet, Manmohan Singh said India is willing to sign on to global target for emission cut if it is accompanied by an equitable burden-sharing ', '2009-11-29 01:01:13'),
(3, 'Gatecrashers met Obama, US Secret Service embarrassed', 'A new photo released by the White House showed a smiling Barack Obama greeting the allegedly uninvited couple, Tareq and Michaele Salahi, at a state dinner hosted for Manmohan Singh.', '2009-11-29 01:28:49'),
(4, 'Madhu Koda arrested in Jharkhand', 'Former Jharkhand chief minister Madhu Koda, who is in the centre of a Rs 2500-crore money laundering scam, was arrested in Chaibasa on Monday, police said.', '2009-12-01 01:37:43'),
(5, 'PM says sorry for Cong MPs foul talk about Atal', 'Prime Minister Manmohan Singh on Wednesday apologised for comments made by Congress MP Beni Prasad Verma against senior Bharatiya Janata Party leaders Atal Bihari Vajpayee and LK Advani in the Lok Sabha. Verma had made personal remarks against Vajpayee and Advani. He had made the remarks during the debate on Liberhan Commission report on Babri Masjid demolition in the Lok Sabha. The BJP had threatened that it would not allow Parliament to function till Verma apologised.', '2009-12-09 23:27:09'),
(6, 'Srishti made very good news', 'Today, Lucas has for the first time seen admin site', '2009-12-11 23:30:14');
